		if ($this->isColumnModified(AdressenPeer::CONTACTID)) $criteria->add(AdressenPeer::CONTACTID, $this->contactid);
		if ($this->isColumnModified(AdressenPeer::ADRESTITEL)) $criteria->add(AdressenPeer::ADRESTITEL, $this->adrestitel);
		if ($this->isColumnModified(AdressenPeer::NAAM)) $criteria->add(AdressenPeer::NAAM, $this->naam);
		if ($this->isColumnModified(AdressenPeer::ATTN)) $criteria->add(AdressenPeer::ATTN, $this->attn);
		if ($this->isColumnModified(AdressenPeer::STRAAT)) $criteria->add(AdressenPeer::STRAAT, $this->straat);
		if ($this->isColumnModified(AdressenPeer::HUISNUMMER)) $criteria->add(AdressenPeer::HUISNUMMER, $this->huisnummer);
