		if ($this->isColumnModified(AdressenPeer::POSTBUS)) $criteria->add(AdressenPeer::POSTBUS, $this->postbus);
		if ($this->isColumnModified(AdressenPeer::PLAATS)) $criteria->add(AdressenPeer::PLAATS, $this->plaats);
