		if ($this->isColumnModified(BankTransactionsPeer::BANK_ACCOUNT_ID)) $criteria->add(BankTransactionsPeer::BANK_ACCOUNT_ID, $this->bank_account_id);
		if ($this->isColumnModified(BankTransactionsPeer::TRANSACTION_DATE)) $criteria->add(BankTransactionsPeer::TRANSACTION_DATE, $this->transaction_date);
		if ($this->isColumnModified(BankTransactionsPeer::NAME)) $criteria->add(BankTransactionsPeer::NAME, $this->name);
		if ($this->isColumnModified(BankTransactionsPeer::AMOUNT)) $criteria->add(BankTransactionsPeer::AMOUNT, $this->amount);
		if ($this->isColumnModified(BankTransactionsPeer::DESCRIPTION)) $criteria->add(BankTransactionsPeer::DESCRIPTION, $this->description);
		if ($this->isColumnModified(BankTransactionsPeer::OTHER_ACCOUNT_NUMBER)) $criteria->add(BankTransactionsPeer::OTHER_ACCOUNT_NUMBER, $this->other_account_number);
		if ($this->isColumnModified(BankTransactionsPeer::CUSTOMERID)) $criteria->add(BankTransactionsPeer::CUSTOMERID, $this->customerid);

		return $criteria;
