	 * request arrays (e.g. $_POST).  This method goes through the column
	 * names, checking to see whether a matching key exists in populated
	 * array. If so the setByName() method is called for that column.
	 *
	 * You can specify the key type of the array by additionally passing one
	 * of the class type constants BasePeer::TYPE_PHPNAME, BasePeer::TYPE_STUDLYPHPNAME,
	 * BasePeer::TYPE_COLNAME, BasePeer::TYPE_FIELDNAME, BasePeer::TYPE_NUM.
	 * The default key type is the column's phpname (e.g. 'AuthorId')
	 *
	 * @param      array  $arr     An array to populate the object from.
	 * @param      string $keyType The type of keys the array uses.
	 * @return     void
	 */
	public function fromArray($arr, $keyType = BasePeer::TYPE_PHPNAME)
	{
		$keys = BoxPeer::getFieldNames($keyType);

		if (array_key_exists($keys[0], $arr)) $this->setBoxId($arr[$keys[0]]);
		if (array_key_exists($keys[1], $arr)) $this->setShipmentId($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setTracking($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setWeightKg($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setLengthCm($arr[$keys[4]]);
		if (array_key_exists($keys[5], $arr)) $this->setWidthCm($arr[$keys[5]]);
		if (array_key_exists($keys[6], $arr)) $this->setHeightCm($arr[$keys[6]]);
		if (array_key_exists($keys[7], $arr)) $this->setVolumeWeightKg($arr[$keys[7]]);
		if (array_key_exists($keys[8], $arr)) $this->setBoxNumber($arr[$keys[8]]);
	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(BoxPeer::DATABASE_NAME);

		if ($this->isColumnModified(BoxPeer::BOX_ID)) $criteria->add(BoxPeer::BOX_ID, $this->box_id);
		if ($this->isColumnModified(BoxPeer::SHIPMENT_ID)) $criteria->add(BoxPeer::SHIPMENT_ID, $this->shipment_id);
		if ($this->isColumnModified(BoxPeer::TRACKING)) $criteria->add(BoxPeer::TRACKING, $this->tracking);
		if ($this->isColumnModified(BoxPeer::WEIGHT_KG)) $criteria->add(BoxPeer::WEIGHT_KG, $this->weight_kg);
		if ($this->isColumnModified(BoxPeer::LENGTH_CM)) $criteria->add(BoxPeer::LENGTH_CM, $this->length_cm);
		if ($this->isColumnModified(BoxPeer::WIDTH_CM)) $criteria->add(BoxPeer::WIDTH_CM, $this->width_cm);
		if ($this->isColumnModified(BoxPeer::HEIGHT_CM)) $criteria->add(BoxPeer::HEIGHT_CM, $this->height_cm);
		if ($this->isColumnModified(BoxPeer::VOLUME_WEIGHT_KG)) $criteria->add(BoxPeer::VOLUME_WEIGHT_KG, $this->volume_weight_kg);
		if ($this->isColumnModified(BoxPeer::BOX_NUMBER)) $criteria->add(BoxPeer::BOX_NUMBER, $this->box_number);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
	 */
	public function buildPkeyCriteria()
	{
		$criteria = new Criteria(BoxPeer::DATABASE_NAME);

		$criteria->add(BoxPeer::BOX_ID, $this->box_id);

		return $criteria;
	}

	/**
	 * Returns the primary key for this object (row).
	 * @return     int
	 */
	public function getPrimaryKey()
	{
		return $this->getBoxId();
	}

	/**
	 * Generic method to set the primary key (box_id column).
	 *
	 * @param      int $key Primary key.
	 * @return     void
	 */
	public function setPrimaryKey($key)
	{
		$this->setBoxId($key);
	}

	/**
	 * Sets contents of passed object to values from current object.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      object $copyObj An object of Box (or compatible) type.
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @throws     PropelException
	 */
	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setShipmentId($this->shipment_id);

		$copyObj->setTracking($this->tracking);

		$copyObj->setWeightKg($this->weight_kg);

		$copyObj->setLengthCm($this->length_cm);

		$copyObj->setWidthCm($this->width_cm);

		$copyObj->setHeightCm($this->height_cm);

		$copyObj->setVolumeWeightKg($this->volume_weight_kg);

		$copyObj->setBoxNumber($this->box_number);


		$copyObj->setNew(true);

		$copyObj->setBoxId(NULL); // this is a auto-increment column, so set to default value

	}

	/**
	 * Makes a copy of this object that will be inserted as a new row in table when saved.
	 * It creates a new object filling in the simple attributes, but skipping any primary
	 * keys that are defined for the table.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @return     Box Clone of current object.
	 * @throws     PropelException
	 */
	public function copy($deepCopy = false)
	{
		// we use get_class(), because this might be a subclass
		$clazz = get_class($this);
		$copyObj = new $clazz();
		$this->copyInto($copyObj, $deepCopy);
		return $copyObj;
	}

	/**
	 * Returns a peer instance associated with this om.
	 *
	 * Since Peer classes are not to have any instance attributes, this method returns the
	 * same instance for all member of this class. The method could therefore
	 * be static, but this would prevent one from overriding the behavior.
	 *
	 * @return     BoxPeer
	 */
	public function getPeer()
	{
		if (self::$peer === null) {
			self::$peer = new BoxPeer();
		}
		return self::$peer;
	}

