	public function hasOnlyDefaultValues()
	{
			// First, ensure that we don't have any columns that have been modified which aren't default columns.
			if (array_diff($this->modifiedColumns, array(PricingPeer::PURCHASE_PRICE,PricingPeer::AMOUNT,PricingPeer::CURRENCYID,PricingPeer::START_DATE,PricingPeer::END_DATE,PricingPeer::CREATED,PricingPeer::CREATED_BY,PricingPeer::CONTACTID,PricingPeer::PRICE_TYPE,PricingPeer::START_NUMBER,PricingPeer::END_NUMBER,PricingPeer::MODIFIED,PricingPeer::MODIFIED_BY))) {
				return false;
			}

			if ($this->purchase_price !== '0.00') {
				return false;
			}

			if ($this->amount !== '0.00') {
				return false;
			}

			if ($this->currencyid !== 2) {
				return false;
			}

			if ($this->start_date !== NULL) {
				return false;
			}

			if ($this->end_date !== NULL) {
				return false;
			}

			if ($this->created !== NULL) {
				return false;
			}

			if ($this->created_by !== 0) {
				return false;
			}

			if ($this->contactid !== 0) {
				return false;
			}

			if ($this->price_type !== 1) {
				return false;
			}

			if ($this->start_number !== 1) {
				return false;
			}

			if ($this->end_number !== 0) {
				return false;
			}

			if ($this->modified !== NULL) {
				return false;
			}

			if ($this->modified_by !== 0) {
				return false;
			}

		// otherwise, everything was equal, so return TRUE
		return true;
	} // hasOnlyDefaultValues()

